allow:
Bash(powershell -Command "
$content = Get-Content -Path ''src/components/InspectionPlanViewer.tsx'' -Raw

# Define the old text pattern (unique enough to match)
$oldPattern = @''
/\*\*
 \* ASTM E2375 Scan Direction Symbol \(.\)
 \* Small outlined square indicating scan/index direction
 \* Can have an optional diagonal line showing scan path
 \*/
function drawScanDirectionSymbol\(
  ctx: CanvasRenderingContext2D,
  x: number,
  y: number,
  hasDiagonal: boolean = false,
  diagonalAngle: number = 45 // degrees
\) \{
  ctx\.save\(\);
  ctx\.strokeStyle = ''#000000'';
  ctx\.fillStyle = ''#000000'';
  ctx\.lineWidth = 1\.5;

  const size = 8;

  // Draw outlined square \(not filled\)
  ctx\.strokeRect\(x - size/2, y - size/2, size, size\);
''@

Write-Output ''Checking if pattern exists in file...''
if ($content -match ''ASTM E2375 Scan Direction Symbol'') {
    Write-Output ''Found pattern!''
} else {
    Write-Output ''Pattern not found''
}
")
